/*Table structure for table `countries` */

DROP TABLE IF EXISTS `countries`;

CREATE TABLE `countries` (
  `countryid` int(3) NOT NULL,
  `regionid` int(3) DEFAULT NULL,
  `isocode` char(12) DEFAULT NULL,
  `name` varchar(255) DEFAULT NULL,
  `link` varchar(255) DEFAULT NULL,
  `history` text,
  `fullname` varchar(255) DEFAULT NULL,
  `capital` varchar(255) DEFAULT NULL,
  `population` int(12) DEFAULT NULL,
  `area` float DEFAULT NULL,
  `density` float DEFAULT NULL,
  `location` text,
  `gdp` float DEFAULT NULL,
  `climate` text,
  `currency` varchar(255) DEFAULT NULL,
  `currency_code` char(9) DEFAULT NULL,
  PRIMARY KEY (`countryid`),
  UNIQUE KEY `idx_link` (`link`),
  KEY `idx_regionid` (`regionid`),
  CONSTRAINT `FK_countries_regions` FOREIGN KEY (`regionid`) REFERENCES `regions` (`regionid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

